import React, { useState } from 'react';
import axios from 'axios';

function App() {
  const [url, setUrl] = useState('');
  const [actions, setActions] = useState('');
  const [result, setResult] = useState('');
  const [error, setError] = useState('');
  const serverPort = 3000; // Always use 3000 for the server

  const handleSubmit = async (e) => {
    e.preventDefault();
    setError('');
    setResult('');
    try {
      let parsedActions;
      try {
        parsedActions = JSON.parse(actions);
      } catch (parseError) {
        throw new Error('Invalid JSON format for actions');
      }

      const response = await axios.post(`http://localhost:${serverPort}/automate`, {
        url,
        actions: parsedActions
      });
      setResult(JSON.stringify(response.data, null, 2));
    } catch (error) {
      console.error('Error:', error);
      setError(error.response?.data?.error || error.message || 'An unexpected error occurred');
    }
  };

  const handleFileUpload = (e) => {
    const file = e.target.files[0];
    if (file) {
      const reader = new FileReader();
      reader.onload = (e) => {
        setActions(e.target.result);
      };
      reader.readAsText(file);
    }
  };

  return (
    <div className="App">
      <h1>Web Interaction Automation</h1>
      <form onSubmit={handleSubmit}>
        <div>
          <label htmlFor="url">URL:</label>
          <input
            type="text"
            id="url"
            value={url}
            onChange={(e) => setUrl(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="actions">Actions (JSON):</label>
          <textarea
            id="actions"
            value={actions}
            onChange={(e) => setActions(e.target.value)}
            required
          />
        </div>
        <div>
          <label htmlFor="file-upload">Or upload JSON file:</label>
          <input
            type="file"
            id="file-upload"
            accept=".json"
            onChange={handleFileUpload}
          />
        </div>
        <button type="submit">Run Automation</button>
      </form>
      {error && (
        <div style={{ color: 'red', marginTop: '10px' }}>
          <h2>Error:</h2>
          <pre>{error}</pre>
        </div>
      )}
      {result && (
        <div style={{ marginTop: '10px' }}>
          <h2>Result:</h2>
          <pre>{result}</pre>
        </div>
      )}
    </div>
  );
}

export default App;
