export function findElementFuzzy(selector) {
  console.log("Searching for element:", selector);
  // Try exact match first
  let element = document.querySelector(selector);
  if (element) {
    console.log("Exact match found:", element);
    return element;
  }

  // If not found, try fuzzy matching
  const allElements = document.querySelectorAll('*');
  let bestMatch = null;
  let highestScore = 0;

  allElements.forEach(el => {
    const score = calculateSimilarity(getElementIdentifiers(el), selector);
    if (score > highestScore) {
      highestScore = score;
      bestMatch = el;
    }
  });

  if (bestMatch) {
    console.log(`Fuzzy match found for "${selector}":`, bestMatch, "with score:", highestScore);
  } else {
    console.log(`No match found for "${selector}"`);
  }

  return bestMatch;
}

export function highlightElement(element, color) {
  const originalOutline = element.style.outline;
  element.style.outline = `2px solid ${color}`;
  setTimeout(() => {
    element.style.outline = originalOutline;
  }, 500);
}

export function simulateMouseEvents(element, events) {
  events.forEach(eventType => {
    const rect = element.getBoundingClientRect();
    const event = new MouseEvent(eventType, {
      view: window,
      bubbles: true,
      cancelable: true,
      clientX: rect.left + rect.width / 2,
      clientY: rect.top + rect.height / 2
    });
    element.dispatchEvent(event);
  });
}

export function waitForOptions(optionValue, optionId, timeout = 5000) {
  return new Promise((resolve, reject) => {
    const startTime = Date.now();
    const intervalId = setInterval(() => {
      let option;
      if (optionId) {
        option = document.querySelector(`#${optionId}`);
      }
      if (!option) {
        const options = document.querySelectorAll('[role="option"]');
        for (let opt of options) {
          if (opt.textContent.trim().toLowerCase().includes(optionValue.toLowerCase())) {
            option = opt;
            break;
          }
        }
      }
      if (option) {
        clearInterval(intervalId);
        resolve(option);
        return;
      }
      if (Date.now() - startTime > timeout) {
        clearInterval(intervalId);
        reject(new Error(`Option "${optionValue}" not found within ${timeout}ms`));
      }
    }, 100);
  });
}

function getElementIdentifiers(element) {
  return [
    element.id,
    element.name,
    element.className,
    element.tagName.toLowerCase(),
    element.getAttribute('data-testid'),
    ...Array.from(element.attributes).map(attr => `${attr.name}="${attr.value}"`)
  ].filter(Boolean).join(' ');
}

function calculateSimilarity(str1, str2) {
  const set1 = new Set(str1.toLowerCase().split(/\W+/));
  const set2 = new Set(str2.toLowerCase().split(/\W+/));
  const intersection = new Set([...set1].filter(x => set2.has(x)));
  return intersection.size / Math.max(set1.size, set2.size);
}
