export function findElementFuzzy(selector) {
  // ... (keep the existing implementation)
}

export function highlightElement(element, color) {
  const originalOutline = element.style.outline;
  element.style.outline = `2px solid ${color}`;
  setTimeout(() => {
    element.style.outline = originalOutline;
  }, 500);
}

export function simulateMouseEvents(element, events) {
  events.forEach(eventType => {
    const rect = element.getBoundingClientRect();
    const event = new MouseEvent(eventType, {
      view: window,
      bubbles: true,
      cancelable: true,
      clientX: rect.left + rect.width / 2,
      clientY: rect.top + rect.height / 2
    });
    element.dispatchEvent(event);
  });
}

export function waitForOptions(optionValue, timeout = 5000) {
  return new Promise((resolve, reject) => {
    const startTime = Date.now();
    const intervalId = setInterval(() => {
      const options = document.querySelectorAll('[role="option"]');
      for (let option of options) {
        if (option.textContent.trim().toLowerCase().includes(optionValue.toLowerCase())) {
          clearInterval(intervalId);
          resolve(option);
          return;
        }
      }
      if (Date.now() - startTime > timeout) {
        clearInterval(intervalId);
        reject(new Error(`Option "${optionValue}" not found within ${timeout}ms`));
      }
    }, 100);
  });
}

function getElementIdentifiers(element) {
  return [
    element.id,
    element.name,
    element.className,
    element.tagName.toLowerCase(),
    element.getAttribute('data-testid'),
    ...Array.from(element.attributes).map(attr => `${attr.name}="${attr.value}"`)
  ].filter(Boolean).join(' ');
}

function calculateSimilarity(str1, str2) {
  const set1 = new Set(str1.toLowerCase().split(/\W+/));
  const set2 = new Set(str2.toLowerCase().split(/\W+/));
  const intersection = new Set([...set1].filter(x => set2.has(x)));
  return intersection.size / Math.max(set1.size, set2.size);
}
